const CX = 128;
const CZ = 128;
const Y_GROUND = 0;

// Colors
const C_BODY = 'yellow_wool';
const C_BEAK = 'orange_wool';
const C_FEET = 'orange_wool';
const C_EYE_BG = 'white_wool';
const C_EYE_PUPIL = 'black_wool';
const C_HAT = 'purple_wool';
const C_HAT_BAND = 'gold_block';
const C_SWORD_BLADE = 'iron_block';
const C_SWORD_GUARD = 'gold_block';
const C_SWORD_HANDLE = 'oak_log';

// Helper: Ellipsoid
function drawEllipsoid(cx, cy, cz, rx, ry, rz, type) {
  for (let y = -ry; y <= ry; y++)
    for (let x = -rx; x <= rx; x++)
      for (let z = -rz; z <= rz; z++) {
        if ((x*x)/(rx*rx) + (y*y)/(ry*ry) + (z*z)/(rz*rz) <= 1.0) {
          block(cx + x, cy + y, cz + z, type);
        }
      }
}

function drawSphere(cx, cy, cz, r, type) {
  drawEllipsoid(cx, cy, cz, r, r, r, type);
}

// 1. FEET
box(CX - 12, Y_GROUND, CZ - 4, CX - 4, Y_GROUND + 2, CZ + 8, C_FEET);
box(CX - 12, Y_GROUND, CZ + 8, CX - 10, Y_GROUND + 2, CZ + 10, C_FEET);
box(CX - 9, Y_GROUND, CZ + 8, CX - 7, Y_GROUND + 2, CZ + 10, C_FEET);
box(CX - 6, Y_GROUND, CZ + 8, CX - 4, Y_GROUND + 2, CZ + 10, C_FEET);

